import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-side-bar',
  templateUrl: './side-bar.component.html',
  styleUrls: ['./side-bar.component.scss'],
})
export class SideBarComponent {
  constructor(private router: Router) {
  }
  currentRoute = this.router.url;

  console.log(currentRoute);
}
<div class="sideBar">
  <div class="nav_list">
    <a href="/dashboard" class="nav_item active">
      <img src="../../../assets/icons/dashboard.svg" />
      <p>Dashboard</p>
    </a>

    <a href="/users" class="nav_item">
      <img src="../../../assets/icons/user.svg" />
      <p>Users</p>
    </a>

    <a href="/orders" class="nav_item">
      <img src="../../../assets/icons/orders.svg" />
      <p>Orders</p>
    </a>

    <a href="/products" class="nav_item">
      <img src="../../../assets/icons/product.svg" />
      <p>Products</p>
    </a>
  </div>
</div>
