import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-product-card',
  templateUrl: './product-card.component.html',
  styleUrls: ['./product-card.component.scss'],
})
export class ProductCardComponent {
  constructor() {}

  @Input() product: any;

  reduction: number = -36;
  price: number = 36.99;
  oldPrice: number = 48.56;

  oneProduct = {
    id: 545454,
    date: '21 June 2020, 12:42 AM',
    title: 'Space for a small ',
    description: 'Space for a small product description',
    price: 82.46,
    quantity: 12,
  };
  addToCartHandler(): void {
    console.log('product added!');
  }
}
<div class="product_card_container">
  <a href="/user/products/details">
    <img src="../../../assets/images/product.jpg"
  /></a>

  <p class="reduction">{{ reduction }} %</p>

  <div class="typo">
    <a href="/products/details"> <p class="product_title">{{oneProduct.title}}</p></a>
    <p class="product_description">{{</p>
  </div>

  <div class="product_card_bottom">
    <div class="price_container">
      <p class="price">{{ price }} USD</p>

      <p class="old_price">{{ oldPrice }}</p>
    </div>

    <button class="product_card_btn" (click)="addToCartHandler()">Add</button>
  </div>
</div>
