  templateUrl: './side-bar.component.html',
  styleUrls: ['./side-bar.component.scss']
})
export class SideBarComponent {

}
<div class="sideBar">
  <div class="logo"><img src="../../../assets/images/logo.png" /></div>

  <div class="nav_list">
    <div class="nav_item">Dashboard</div>
