import { NgModule } from '@angular/core';
import { RouterModule } from '@angular/router';
import { AuthGuard } from '../../guard/auth.guard';

import { ViewHomeComponent } from './view-home/view-home.component';

const routes = [
  {
    path: 'view',
    component: ViewMaterialComponent,
    canActivate: [AuthGuard, PermissionGuard],
    data: {
      permission: 'Material_Permission_Read',
    },
  },
  {
    path: 'add',
    component: AddMaterialComponent,
    canActivate: [AuthGuard, PermissionGuard],
    data: {
      permission: 'Material_Permission_Create',
    },
  },
  {
    path: 'edit/:id',
    component: EditMaterialComponent,
    canActivate: [AuthGuard, PermissionGuard],
    data: {
      permission: 'Material_Permission_Edit',
    },
  },
  { path: '', redirectTo: 'view', pathMatch: 'full' },
];

@NgModule({
  declarations: [
    ViewMaterialComponent,
    AddMaterialComponent,
    EditMaterialComponent,
  ],
  imports: [RouterModule.forChild(routes), TranslateModule, CoreCommonModule],
  exports: [ViewMaterialComponent, AddMaterialComponent, EditMaterialComponent],
})
export class Module {}
