import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss'],
})
export class LoginComponent {
  email: string = '';
  password: string = '';

  login() {
    // Implement your login logic here
    if (this.email === 'user@example.com' && this.password === 'password') {
      // Successful login, navigate to the dashboard or home page
      // You can use Angular Router for navigation

      AuthService.login()
    } else {
      // Handle login failure (e.g., show an error message)
    }
  }
}
<div class="login-container">
  <div class="login-card">
    <h2 class="login-card-title">Sign in</h2>
    <p class="login-card-subtitle">Sign in to your account using email and password provided during registration.</p>

    <div class="login-card-input-container">
      <i class="fa-solid fa-envelope login-card-input-icon"></i>
      <input type="text" placeholder="Enter your email" class="login-card-input">
    </div>

    <div class="login-card-input-container">
      <i class="fa-solid fa-lock login-card-input-icon"></i>
      <input type="password" placeholder="Enter your password" class="login-card-input">
    </div>

    <div class="login-card-forgot-password-container">
      <div class="login-card-forgot-password-checkbox">
        <input type="checkbox" id="keep-me" class="login-card-forgot-password-checkbox-input" />
        <label for="keep-me">Keep me</label>
      </div>

      <p class="login-card-forgot-password-p" routerLink="/auth/forgot-password">Forgot password?</p>
    </div>

    <button type="button" class="login-card-btn" (click)="login()">Sign in</button>

    <div  class="login-card-account">Don't have an account? <span routerLink="/auth/register" class="login-card-account-signup">Sign up</span></div>

    <hr class="login-card-devider">

    <h4 class="login-card-or">Or sign up with</h4>

    <div class="login-card-btn-group">
      <div class="login-card-btn-group-btn">
        <i class="fa-brands fa-facebook-f"></i>
      </div>
      <div class="login-card-btn-group-btn">
        <i class="fa-brands fa-twitter"></i>
      </div>
      <div class="login-card-btn-group-btn">
        <i class="fa-brands fa-github"></i>
      </div>
      <div class="login-card-btn-group-btn">
        <i class="fa-brands fa-google"></i>
      </div>
    </div>
  </div>

  <img src="/assets/images/Rectangle 57.png" alt="shape" class="login-container-shape-1">
  <img src="/assets/images/Rectangle 58.png" alt="shape" class="login-container-shape-2">
</div>
