import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
})
export class HeaderComponent {
  pageslist = ['SHOP', 'COLLECTIVE', 'DESIGNERS', 'ABOUT US', 'CONTACT US'];

  isAdminViews = true;
}
<header class="header">
  <div class="header_left" ngif>
    <a href="/"><img src="../../../assets/images/logo.png" class="logo" /></a>

    <ul class="nav_list">
      <li *ngFor="let page of pageslist">
        <a href="/products">{{ page }}</a>
      </li>
    </ul>

    <img src="../../../assets/icons/menu.svg" />
  </div>

  <div class="header_right">
    <a href="/cart"> <img src="../../../assets/icons/cart_icon.svg" /></a>
  </div>
</header>
